INSERT INTO wp_posts VALUES('1032', '1', '2012-11-13 18:04:01', '2012-11-13 18:04:01', '<em>Everyday Listening Products are subtle and convenient devices that assist hearing “everyday products” including: Telephones, Televisions, Doorbells, even Alarm Clocks.</em>\n\nThese hearing improvement devices send sound directly to your hearing aid. And, thanks to advances (such as Bluetooth) they’ve never been easier to use.\n\n[caption id=\"attachment_1041\" align=\"alignleft\" width=\"81\"]<a href=\"http://agdev.ca/crystalhearing/wp-content/uploads/2012/09/Phonak_TVLinkS100.jpg\"><img class=\" wp-image-1041\" title=\"Phonak_TVLinkS100\" src=\"http://agdev.ca/crystalhearing/wp-content/uploads/2012/09/Phonak_TVLinkS100.jpg\" alt=\"\" width=\"81\" height=\"81\" /></a> TVLinkS[/caption]\n\n[caption id=\"attachment_1040\" align=\"alignleft\" width=\"81\"]<a href=\"http://agdev.ca/crystalhearing/wp-content/uploads/2012/09/Phonak_EasyLink100.jpg\"><img class=\" wp-image-1040\" title=\"Phonak_EasyLink100\" src=\"http://agdev.ca/crystalhearing/wp-content/uploads/2012/09/Phonak_EasyLink100.jpg\" alt=\"\" width=\"81\" height=\"81\" /></a> EasyLink[/caption]\n\n[caption id=\"attachment_1039\" align=\"alignleft\" width=\"82\"]<a href=\"http://agdev.ca/crystalhearing/wp-content/uploads/2012/09/Phonak_ComPilot100.jpg\"><img class=\" wp-image-1039\" title=\"Phonak_ComPilot100\" src=\"http://agdev.ca/crystalhearing/wp-content/uploads/2012/09/Phonak_ComPilot100.jpg\" alt=\"\" width=\"82\" height=\"82\" /></a> ComPilot[/caption]', 'Everyday Listening Products', '', 'inherit', 'open', 'open', '', '403-autosave', '', '', '2012-11-13 18:04:01', '2012-11-13 18:04:01', '', '403', 'http://agdev.ca/crystalhearing/403-autosave/', '0', 'revision', '', '0');
