INSERT INTO wp_posts VALUES('357', '1', '2012-09-05 22:25:49', '2012-09-05 22:25:49', '<em>A hearing evaluation is a comprehensive assessment of your aural health. The evaluation determines the extent of the hearing loss and available treatment options.</em>\r\n\r\nWe start with a consultation covering your medical and hearing history, issues you are currently facing, past challenges and background information. Next, we physically examine the ear. Then we conduct a series of tests to determine the cause and degree of the loss.\r\n\r\nAfter your evaluation, we will take you through each of the available options and help you choose the best hearing system for your needs. We want you to leave fully satisfied and hearing the best you can.', 'Hearing Evaluation', '', 'publish', 'open', 'open', '', 'hearing-evaluation', '', '', '2012-09-11 21:33:43', '2012-09-11 21:33:43', '', '348', 'http://agdev.ca/crystalhearing/?page_id=357', '0', 'page', '', '0');
