INSERT INTO wp_posts VALUES('363', '1', '2012-09-05 22:29:04', '2012-09-05 22:29:04', 'Some things to remember: avoid heat, cold, chemicals and above all moisture. Do not store your hearing aid near heat sources such as stoves, fireplaces or even in direct sunlight. Saunas, pools, showers, rain and even perspiration can potentially damage a devise. If it does get wet, open it and allow it to dry thoroughly. And, of course, try not to drop it.<strong></strong>', 'Handling & Storage', '', 'publish', 'open', 'open', '', 'handling-storage', '', '', '2012-09-11 22:03:54', '2012-09-11 22:03:54', '', '350', 'http://agdev.ca/crystalhearing/?page_id=363', '0', 'page', '', '0');
