INSERT INTO wp_posts VALUES('368', '1', '2012-09-05 22:33:05', '2012-09-05 22:33:05', '<em>Our ears are truly remarkable. When a sound occurs – a voice, an alarm, a car engine – vibrations are formed. It’s those vibrations that our ears pick up and translate for our brain to turn into sound.</em>\r\n\r\nThe canal, drum, hammer, anvil, stirrup, cochlea, and the nerves that send the information to the brain, it’s absolutely amazing how it all works.', 'How We Hear', '', 'publish', 'open', 'open', '', 'how-we-hea', '', '', '2012-09-11 22:05:15', '2012-09-11 22:05:15', '', '352', 'http://agdev.ca/crystalhearing/?page_id=368', '0', 'page', '', '0');
