INSERT INTO wp_posts VALUES('375', '1', '2012-09-05 22:35:52', '2012-09-05 22:35:52', '<em>Depending on the severity of the hearing loss there are many treatment options available.</em>\r\n\r\nSurgery, listening devices, hearing aids, cochlear implants, signaling and text displays are just a few of the treatments that can help your hearing loss and increase your quality of life. Talk to your hearing specialist to figure out the best option for you.', 'Hearing Loss Treatments', '', 'publish', 'open', 'open', '', 'hearing-loss-treatments', '', '', '2012-10-30 22:03:58', '2012-10-30 22:03:58', '', '0', 'http://agdev.ca/crystalhearing/?page_id=375', '0', 'page', '', '0');
