INSERT INTO wp_posts VALUES('484', '1', '2012-10-11 18:53:01', '2012-10-11 18:53:01', '<em>Hearing technology has taken a quantum leap forward in recent years. There are now hearing aids that can fit any shape of ear and improve nearly every problem.</em>\n\nOver the ear, behind the ear, half-shell, open fit, in the canal, completely in the canal – the list goes on and on. Crystal Hearing carries all of the top brands in hearing aid and digital assistive technologies. Our hearing professionals are extensively trained in picking the right product for your needs.\n\n<a class=\"cta-link\" title=\"Hearing Solutions\" href=\"http://agdev.ca/crystalhearing/products/hearing-aids/\">View Our Selection of Hearing Products</a>', 'Hearing Aids', '', 'inherit', 'open', 'open', '', '350-autosave', '', '', '2012-10-11 18:53:01', '2012-10-11 18:53:01', '', '350', 'http://agdev.ca/crystalhearing/350-autosave/', '0', 'revision', '', '0');
