INSERT INTO wp_posts VALUES('408', '1', '2012-09-11 16:58:05', '2012-09-11 16:58:05', '<em>With conductive hearing the inner ear is not effectively receiving sound. There is damage, blockage or some other issue jamming the signal from the outer and/or middle ear.</em>\r\n\r\nFortunately, it is possible to improve and even cure the hearing loss either medically or surgically. Even if surgery is not an option a hearing aid can improve sound quality dramatically.', 'Conductive Hearing Loss', '', 'publish', 'open', 'open', '', 'conductive-hearing-loss', '', '', '2012-09-11 22:07:13', '2012-09-11 22:07:13', '', '370', 'http://agdev.ca/crystalhearing/?page_id=408', '0', 'page', '', '0');
