INSERT INTO wp_posts VALUES('412', '1', '2012-09-11 16:59:02', '2012-09-11 16:59:02', '<em>Mixed hearing loss is when a person has a combination of two or more types of hearing loss. This category is when both sensorineural hearing loss and conductive hearing loss are present.</em>\r\n\r\nBecause both the middle and inner ear are affected, often the conductive hearing loss may be treated, however, the sensorineural hearing loss is frequently permanent.', 'Mixed Hearing Loss', '', 'publish', 'open', 'open', '', 'mixed-hearing-loss', '', '', '2012-09-11 22:08:21', '2012-09-11 22:08:21', '', '370', 'http://agdev.ca/crystalhearing/?page_id=412', '0', 'page', '', '0');
