INSERT INTO wp_posts VALUES('414', '1', '2012-09-11 16:59:41', '2012-09-11 16:59:41', '<em>The term <strong>congenital hearing loss</strong> means the hearing loss is present at birth. It is caused by several genetic and non-genetic factors.</em>\r\n\r\nSome non-genetic factors include: infection, prematurity, low birth weight, drugs and alcohol consumed during pregnancy, lack of oxygen and maternal diabetes. Genetic factors can occur when one or both of the parents carry the recessive gene and pass it on to their child.', 'Hearing Loss at Birth', '', 'publish', 'open', 'open', '', 'hearing-loss-at-birth', '', '', '2012-09-11 22:10:29', '2012-09-11 22:10:29', '', '372', 'http://agdev.ca/crystalhearing/?page_id=414', '0', 'page', '', '0');
