INSERT INTO wp_posts VALUES('940', '1', '2012-10-09 20:17:05', '2012-10-09 20:17:05', 'Our employees are one of the valued assets of our company. We look for people who want to be part of a high quality team, and who are committed to ongoing education and improvement. Please contact us at <a href=\"mailto:jobs@crystalvisionandhearing.com\">jobs@crystalvisionandhearing.com</a> if you are interested in learning more about employment opportunities with Crystal Vision &amp; Hearing.\r\n\r\n<strong>Do you have 2 years post secondary education?</strong>\r\n\r\nWe are currently looking to sign up a hearing apprentice. In order to qualify for the apprenticeship you MUST have 2 years post seconday education in almost anything.\r\n<ul>\r\n	<li>The right candidate must appreciate seniors and have a great personality.</li>\r\n	<li>If you would like more information on our company please check out our website at <a title=\"Crystal Vision and Hearing\" href=\"http://www.cvoh.ca\" target=\"_blank\">www.cvoh.ca</a></li>\r\n	<li>If you would like some info on the apprenticeship please check out <a href=\"http://www.cshhpbc.org/docs/guidelines%20for%20hip%20students.pdf\">http://www.cshhpbc.org/docs/guidelines%20for%20hip%20students.pdf</a></li>\r\n	<li>If you would like to meet and discuss this opportunity please send your resume soon as we want to start apprenticing asap.</li>\r\n</ul>', 'Careers', '', 'inherit', 'open', 'open', '', '39-revision-4', '', '', '2012-10-09 20:17:05', '2012-10-09 20:17:05', '', '39', 'http://agdev.ca/crystalhearing/39-revision-4/', '0', 'revision', '', '0');
