INSERT INTO wp_posts VALUES('1100', '1', '2012-11-01 20:20:18', '2012-11-01 20:20:18', '<em>Just like a contact lens for your ear – invisible hearing aids are so small that you might even forget that you’re wearing them.</em>\r\n\r\nCrystal Hearing is proud to carry the Nu-Ear MiniScopic, an <strong>absolutely 100% invisible hearing aid</strong>. The MiniScopic is a deep insertion hearing aid that is customized to fit your ear\'s canal. Miniscopic\'s state-of-the-art digital technology is 100% programmable and is available for a variety of hearing loss.\r\n\r\n<a href=\"http://www.youtube.com/watch?v=8uPlJQzYlIo&amp;feature=plcp\"><img class=\"alignnone size-full wp-image-905\" title=\"nuear miniscopic\" src=\"http://agdev.ca/crystalhearing/wp-content/uploads/2012/11/nuear-miniscopic.png\" alt=\"\" width=\"210\" height=\"210\" /></a>', 'Invisible Hearing Aids', '', 'inherit', 'open', 'open', '', '400-revision-5', '', '', '2012-11-01 20:20:18', '2012-11-01 20:20:18', '', '400', 'http://agdev.ca/crystalhearing/400-revision-5/', '0', 'revision', '', '0');
