INSERT INTO wp_posts VALUES('189', '1', '2012-07-27 17:10:07', '2012-07-27 17:10:07', '<em>Nearsightedness, also known as myopia, refers to seeing well at a close range, but not at a distance. Light entering the eye is focused incorrectly, making objects at a distant appear blurry. It is the most common eye condition.</em>\r\n\r\nNearsightedness is not a disease; it is a partially genetic disorder that affects the shape of the eye. Often the ability to focus and see at a distance weakens in childhood and continues to degrade until the early/mid twenties where it begins to stabilize.', 'Near Sightedness', '', 'publish', 'open', 'open', '', 'near-sightedness', '', '', '2012-08-17 16:43:09', '2012-08-17 16:43:09', '', '58', 'http://agdev.ca/crystalvision/?page_id=189', '0', 'page', '', '0');
