INSERT INTO wp_posts VALUES('191', '1', '2012-07-27 17:11:04', '2012-07-27 17:11:04', '<em>Farsightedness, also known as hyperopia, refers to seeing well at a distance, but having difficulty seeing objects that are at a close range. It is caused by the focusing power of the eye being too weak or incorrectly focusing behind the retina.</em>\r\n\r\nAs with nearsightedness, farsightedness is a partially genetic disorder. If parents are susceptible, their children are more likely to become farsighted as well. However, many children outgrow the condition.', 'Far Sightedness', '', 'publish', 'open', 'open', '', 'far-sightedness', '', '', '2012-08-17 16:44:28', '2012-08-17 16:44:28', '', '58', 'http://agdev.ca/crystalvision/?page_id=191', '0', 'page', '', '0');
