INSERT INTO wp_posts VALUES('193', '1', '2012-07-27 17:11:23', '2012-07-27 17:11:23', '<em>Astigmatism makes it difficult to focus and see fine details, either close up or from a distance. It is caused by an abnormally curved cornea, causing light to be incorrectly focused in the eye.</em>\r\n\r\nAstigmatism is very common, often occurring alongside nearsighted and farsighted eyes. As well, astigmatism may develop after some types of eye surgeries.', 'Astigmatism', '', 'publish', 'open', 'open', '', 'astigmatism', '', '', '2012-08-17 16:45:22', '2012-08-17 16:45:22', '', '58', 'http://agdev.ca/crystalvision/?page_id=193', '0', 'page', '', '0');
