INSERT INTO wp_posts VALUES('197', '1', '2012-07-27 17:12:04', '2012-07-27 17:12:04', '<em>Glaucoma is a disease that leads to optic nerve damage. Once lost, vision cannot normally be recovered, so treatment is aimed at preventing further loss.  </em>\r\n\r\nThere are different types of glaucoma, however, in most cases the damage is due to increased pressure in the eye. It is the second most common cause of blindness after cataracts.', 'Glaucoma', '', 'publish', 'open', 'open', '', 'glaucoma', '', '', '2012-08-17 16:47:35', '2012-08-17 16:47:35', '', '58', 'http://agdev.ca/crystalvision/?page_id=197', '0', 'page', '', '0');
