INSERT INTO wp_posts VALUES('199', '1', '2012-07-27 17:12:23', '2012-07-27 17:12:23', '<em>Macular degeneration is a disease most common in people over the age of 60. It is a slow degeneration in the center of the visual field. Peripheral vision remains, however it is a major impairment in older adults.</em>\r\n\r\nMacular degeneration makes it difficult to see fine details, read, or even recognize a face until it is close. Although some macular diseases affect younger people, the term and disease usually refers to age-related macular degeneration.', 'Macular Degeneration', '', 'publish', 'open', 'open', '', 'macular-degeneration', '', '', '2012-08-17 16:48:43', '2012-08-17 16:48:43', '', '58', 'http://agdev.ca/crystalvision/?page_id=199', '0', 'page', '', '0');
